mapper.select:
SELECT a.id,a.userId,a.award,a.context,a.job_type, DATE_FORMAT(a.createTime,"%Y-%m-%d")AS createTime,b.nickName,b.avatarUrl FROM beg_job a LEFT JOIN admin b ON a.userId = b.userId WHERE a.id = any(select jobId from beg_job_record c where c.userId=#{userId})
AND id < #{id}
ORDER BY id DESC LIMIT 10

SELECT a.id,a.userId,a.award,a.context,a.job_type, DATE_FORMAT(a.createTime,"%Y-%m-%d")AS createTime,b.nickName,b.avatarUrl, IF(COUNT(c.jobId) IS NULL,0,COUNT(c.jobId))AS peopleCount FROM beg_job a LEFT JOIN admin b ON a.userId = b.userId LEFT JOIN beg_job_record c ON a.id = c.jobId WHERE a.userId = #{userId} GROUP BY a.id
AND id < #{id}
ORDER BY id DESC LIMIT 10

SELECT a.id,a.userId,a.award,a.alreadyAward,a.totalCount,a.alreadyCount,a.context, DATE_FORMAT(a.createTime,"%Y-%m-%d")AS createTime,a.state,a.headPic, a.title,a.userName,b.nickName,b.avatarUrl,IF(c.markCount IS NULL,0,c.markCount)AS markCount FROM beg_job a LEFT JOIN admin b ON a.userId = b.userId LEFT JOIN (SELECT COUNT(1)+903 AS markCount,jobId FROM (SELECT DISTINCT(userId),jobId FROM job_take)a GROUP BY jobId)c ON a.id = c.jobId WHERE a.state = 4
AND id < #{id}
ORDER BY id DESC LIMIT 2
