mapper.select:
SELECT a.id,a.userId,a.award,a.alreadyAward,a.totalCount,a.alreadyCount,a.context, DATE_FORMAT(a.createTime,"%Y-%m-%d")AS createTime,a.state,b.nickName,b.avatarUrl FROM job a LEFT JOIN admin b ON a.userId = b.userId WHERE a.userId = #{userId} AND a.state IN(1,2,3)
AND id < #{id}
ORDER BY id DESC LIMIT 10

SELECT a.id,a.userId,a.award,a.alreadyAward,a.totalCount,a.alreadyCount,a.context, DATE_FORMAT(a.createTime,"%Y-%m-%d")AS createTime,a.state,a.headPic, a.title,a.userName,b.nickName,b.avatarUrl,IF(c.markCount IS NULL,0,c.markCount)AS markCount FROM job a LEFT JOIN admin b ON a.userId = b.userId LEFT JOIN (SELECT COUNT(1)+903 AS markCount,jobId FROM (SELECT DISTINCT(userId),jobId FROM job_take)a GROUP BY jobId)c ON a.id = c.jobId WHERE a.state = 4
AND id < #{id}
ORDER BY id DESC LIMIT 2
